// Combine all competitor data
const ahrefsData = $input.first()?.json || {};
const semrushData = $input.all().find(item => item.json?.domain) || {};
const buzzsumoData = $input.all().find(item => item.json?.results) || {};

// Process competitor intelligence
const competitorIntelligence = {
  timestamp: new Date().toISOString(),
  domain: ahrefsData.target || 'N/A',
  traffic_estimate: ahrefsData.traffic?.organic?.value || 0,
  backlinks: ahrefsData.backlinks?.total || 0,
  top_keywords: semrushData.json?.keywords?.slice(0, 10) || [],
  viral_content: buzzsumoData.json?.results?.slice(0, 5) || [],
  content_gaps: [],
  publishing_frequency: '5-7 posts/week' // This would be calculated from actual data
};

// Identify content gaps (simplified logic)
if (buzzsumoData.json?.results) {
  const competitorTopics = buzzsumoData.json.results.map(article => article.title);
  competitorIntelligence.content_gaps = [
    'Sustainable packaging solutions',
    'Circular economy in fashion',
    'Eco-friendly manufacturing processes'
  ];
}

return [{ json: competitorIntelligence }];

// Combine trends and keyword data
const trendsData = $input.all().find(item => item.json?.default) || {};
const questionsData = $input.all().find(item => item.json?.questions) || {};

// Process keyword opportunities
const keywordOpportunities = {
  timestamp: new Date().toISOString(),
  trending_keywords: [],
  long_tail_questions: [],
  search_volume_trends: [],
  seasonal_patterns: [],
  content_opportunities: []
};

// Extract trending keywords from Google Trends
if (trendsData.json?.default?.timelineData) {
  keywordOpportunities.trending_keywords = trendsData.json.default.timelineData
    .slice(0, 10)
    .map(item => ({
      keyword: item.formattedValue || 'Unknown',
      trend_velocity: item.value?.[0] || 0,
      region: 'US'
    }));
}

// Extract questions from AnswerThePublic
if (questionsData.json?.questions) {
  keywordOpportunities.long_tail_questions = questionsData.json.questions
    .slice(0, 15)
    .map(q => ({
      question: q.question || q,
      search_volume: Math.floor(Math.random() * 1000) + 100, // Placeholder
      difficulty: Math.floor(Math.random() * 100) + 1,
      suggested_format: ['blog', 'video', 'infographic'][Math.floor(Math.random() * 3)]
    }));
}

// Generate content opportunities
keywordOpportunities.content_opportunities = [
  {
    title: '5 Sustainable Fashion Trends Taking Over 2024',
    keyword_target: 'sustainable fashion trends',
    estimated_traffic: 2500,
    content_type: 'blog',
    priority: 'high'
  },
  {
    title: 'How to Build a Circular Economy Wardrobe',
    keyword_target: 'circular economy fashion',
    estimated_traffic: 1800,
    content_type: 'guide',
    priority: 'medium'
  }
];

return [{ json: keywordOpportunities }];

// Process Reddit audience insights
const redditData = $input.first()?.json || {};

const audienceInsights = {
  timestamp: new Date().toISOString(),
  top_pain_points: [],
  common_questions: [],
  sentiment_analysis: 'neutral',
  engagement_topics: [],
  regional_preferences: {}
};

// Extract pain points from Reddit posts
if (redditData.data?.children) {
  const posts = redditData.data.children;

  audienceInsights.top_pain_points = posts
    .filter(post => post.data.title.includes('problem') || post.data.title.includes('issue'))
    .slice(0, 10)
    .map(post => ({
      pain_point: post.data.title,
      upvotes: post.data.ups || 0,
      comments: post.data.num_comments || 0,
      source: 'reddit'
    }));

  audienceInsights.common_questions = posts
    .filter(post => post.data.title.includes('?') || post.data.title.toLowerCase().includes('how'))
    .slice(0, 10)
    .map(post => ({
      question: post.data.title,
      engagement_score: (post.data.ups || 0) + (post.data.num_comments || 0),
      category: 'general'
    }));

  audienceInsights.engagement_topics = posts
    .sort((a, b) => (b.data.ups || 0) - (a.data.ups || 0))
    .slice(0, 5)
    .map(post => ({
      topic: post.data.title,
      engagement_score: post.data.ups || 0,
      discussion_level: post.data.num_comments || 0
    }));
}

// Add some sample regional preferences
audienceInsights.regional_preferences = {
  'US': ['affordability', 'brand transparency'],
  'EU': ['sustainability certifications', 'local production'],
  'APAC': ['quality', 'innovation']
};

return [{ json: audienceInsights }];
